pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
gql: "query getFooter {
    getfooter: site {
        content {
            renderedRegionsByPageTypeAndEntityId(entityId: 476, entityPageType: PAGE) {
                regions {
                    name
                    html
                }
            }
        }
    },
    getMegaNav: site {
        content {
            renderedRegionsByPageTypeAndEntityId(entityId: 475, entityPageType: PAGE) {
                regions {
                    name
                    html
                }
            }
        }
    },
    customerWishlist:customer{
        wishlists{
            edges{
                node{
                    isPublic
                    entityId
                    name
                }
            }
        }
    }
}"
---
{{#partial "page"}}
    {{inject "currentWishlistId" gql.data.customerWishlist.wishlists.edges.0.node.entityId}}
    <script>
        let jsContext = JSON.parse({{jsContext}});
        const $currentWishlistId = jsContext.currentWishlistId;
    </script>

    {{#each product.shipping_messages}}
        {{> components/common/alert-info message}}
    {{/each}}
    <div class="productView-container">
        <div class="container">
            {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
        </div>
        <div class="container-full" itemscope itemtype="http://schema.org/Product">
            <div class="container">
                <div >
                    {{> components/products/product-view schema=true }}
                </div>
            </div>
            <div class="container bg-white">
                {{{region name="product_below_content"}}}
  
                {{#if product.videos.list.length}}
                    {{> components/products/videos product.videos}}
                {{/if}}

                {{#all settings.show_product_reviews (if theme_settings.show_product_reviews_tabs '!==' true)}}
                    {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
                {{/all}}

                {{#if product.related_products}}
                    <section class="carousel-section">
                        <h2 class="h2" >{{lang 'products.related_products'}}</h2>
                        {{#if settings.data_tag_enabled}}
                            {{> components/products/carousel products=product.related_products columns=5 list="You may also like..."}}
                        {{else}}
                            {{> components/products/carousel products=product.related_products columns=5}}
                        {{/if}}
                    </section>
                {{/if}}


                {{#if customer}}
                    <section class="carousel-section recently-viewed">
                        <h2 class="h2">Recently Viewed Products</h2>
                        <div id="recently-viewed-section" class="productCarouselRecentlyViewed owl-carousel" data-list-name="Recently Viewed Products">
                            
                        </div>
                    </section>
                {{/if}}
            </div>
        </div>
    </div>
{{/partial}}
{{> layout/base-full}}

components/common/breadcrumbs:
{{#unless theme_settings.hide_breadcrumbs }}
<div class="pt-3">
    <div class="breadcrumbs">
        <div class="container mt-1">
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                
                {{#each breadcrumbs}}
                    {{#if ../page_type '==' 'wishlist'}}
                        {{#if @last}}
                        <li class="breadcrumb-item {{#if @last}}active{{/if}}">
                            <a href="{{../../../urls.account.wishlists.all}}">
                                <span>{{lang 'account.nav.wishlists' num_wishlists=customer.num_wishlists}}</span>
                            </a>
                        </li>
                        {{/if}}
                    {{/if}}                
                    <li class="breadcrumb-item {{#if @last}}active{{/if}}" {{#if @last}}aria-current="page" {{/if}}>
                        {{#or @last (if url "==" null)}}
                            
                            <meta itemprop="item" content="{{url}}">
                            <span itemprop="name">{{name}}</span>
                        {{else}}
                            <a href="{{url}}" itemprop="item"><span itemprop="name">{{name}}</span></a>
                        {{/or}}
                        <meta itemprop="position" content="{{add @index 1}}" />
                    </li>
                    {{/each}}

                    
                </ol>
            </nav>
        </div>
    </div>
</div>
{{/unless}}

