checkout/customcheckout:
{{#partial "head"}}

{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}

<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

{{{head.scripts}}}

{{/partial}}

{{#partial "page"}}
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

{{{ checkout.checkout_content }}}

{{{ footer.scripts }}}

<script type="text/javascript">
    let $t = setInterval(() => {
        
        if(document.getElementsByClassName("shippingOptions-container").length !=0){
            // clearInterval($t);

            document.querySelectorAll('.shippingOption-desc').forEach(function(elm) {
                let text = elm.innerHTML;
                text = text.replace('Shipping (','');
                text = text.replace('- (','');
                text = text.replace('))',')');
                elm.innerHTML = text;
            });
        }

    }, 100);

</script>
{{/partial}}

{{> layout/empty}}

pages/checkout:
{{#comment}}
    Change this to:
        `checkout/paymentputty` for the paymentputty, react checkout.
        `checkout/onepage` for the customized javascript checkout.
        `checkout/customcheckout` for the customized BigCommerce react checkout.
{{/comment}}
{{> checkout/customcheckout }}
