components/common/navigation-list:
{{#if children}}
<a class="nav-link has-submenu{{#if is_active}} active{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}">
    {{name}} <span class="more"><i class="fas fa-plus" aria-hidden="true"></i></span>
</a>
<div class="nav-submenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="nav">
        <li class="nav-item">
            <a class="nav-link" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="nav-item">
                {{#if children}}
                    <a class="nav-link{{#if is_active}} active{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}" data-collapsible-disabled-breakpoint="medium" data-collapsible-disabled-state="open" data-collapsible-enabled-state="closed">
                        {{name}} <span class="more"><i class="fas fa-plus" aria-hidden="true"></i></span>
                    </a>
                    <ul class="nav nav-submenu" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="nav-item">
                            <a class="nav-link{{#if is_active}} active{{/if}}" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="nav-link{{#if is_active}} active{{/if}}" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="nav-link{{#if is_active}} active{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}
