pages/account/recent-items:
 

{{#partial "page"}}
<div class="account container">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    {{> components/account/navigation account_page='recent_items'}}
    <h2 class=" ">{{lang 'account.recent_items.heading' }}</h2>

    {{#unless customer.recently_viewed_products.items}}
        {{> components/common/alert-info (lang 'account.recent_items.no_items')}}
    {{/unless}}

    <form action="{{urls.compare}}" method="post" class="grid-layout py-3">
        {{> components/products/grid products=customer.recently_viewed_products.items show_compare=customer.recently_viewed_products.show_compare gql=gql}}
    </form>
</div>
{{/partial}}
{{> layout/base}}

components/common/breadcrumbs:
{{#unless theme_settings.hide_breadcrumbs }}
<div class="pt-3">
    <div class="breadcrumbs">
        <div class="container mt-1">
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                
                {{#each breadcrumbs}}
                    {{#if ../page_type '==' 'wishlist'}}
                        {{#if @last}}
                        <li class="breadcrumb-item {{#if @last}}active{{/if}}">
                            <a href="{{../../../urls.account.wishlists.all}}">
                                <span>{{lang 'account.nav.wishlists' num_wishlists=customer.num_wishlists}}</span>
                            </a>
                        </li>
                        {{/if}}
                    {{/if}}                
                    <li class="breadcrumb-item {{#if @last}}active{{/if}}" {{#if @last}}aria-current="page" {{/if}}>
                        {{#or @last (if url "==" null)}}
                            
                            <meta itemprop="item" content="{{url}}">
                            <span itemprop="name">{{name}}</span>
                        {{else}}
                            <a href="{{url}}" itemprop="item"><span itemprop="name">{{name}}</span></a>
                        {{/or}}
                        <meta itemprop="position" content="{{add @index 1}}" />
                    </li>
                    {{/each}}

                    
                </ol>
            </nav>
        </div>
    </div>
</div>
{{/unless}}

