./mojo/theme.scss:
$white:    #fff;                                                  // these colors don't typically change
$gray-100: #f5f7fa;                                               // check out the Main Colors section
$gray-200: #e9ecef;                                               // to adjust the site colors
$gray-300: #dee2e6;
$gray-400: #ced4da;
$gray-500: #adb5bd;
$gray-600: #6c757d;
$gray-700: #495057;
$gray-800: #343a40;
$gray-900: #212529;
$black:    #000;

$blue:    #007bff;
$indigo:  #6610f2;
$purple:  #6f42c1;
$pink:    #e83e8c;
$red:     #dc3545;
$orange:  #fd7e14;
$yellow:  #ffc107;
$green:   #28a745;
$teal:    #20c997;
$cyan:    #17a2b8;
$gray:        $gray-500;

$theme-primary:       stencilColor("color-primary");        // Primary
$theme-secondary:     stencilColor("color-secondary");      // Secondary
$theme-light:         stencilColor("color-greyLightest");   // Light
$theme-dark:          $gray-800;                  			
$theme-success:       stencilColor("color-success");        // Call to Action 1
$theme-info:          stencilColor("color-info");           // Call to Action 2
$theme-danger:        stencilColor("color-error");          // Call to Action 3
$theme-warning:       stencilColor("color-warning");
$theme-default:       $gray-600;

./mojo/mixins.scss:
@mixin lazy-loaded-img() {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
}

@mixin lazy-loaded-padding($size-param) {
  &:after {
    content: "";
    display: block;
    height: 0;
    width: 100%;
    padding-bottom: get-padding(stencilString($size-param));
  }
}

// Rem Calculations
// -----------------------------------------------------------------------------

@function stripUnit($num) {
  @return $num / ($num * 0 + 1);
}

// scss-lint:disable ZeroUnit
@function convertToRem($value, $base-value: 16px) {
  $value: stripUnit($value) / stripUnit($base-value) * 1rem;
  @if ($value == 0rem) { $value: 0; } // Turn 0rem into 0
  @return $value;
}

@function remCalc($values, $base-value: 16px) {

  $max: length($values);

  @if $max == 1 { @return convertToRem(nth($values, 1), $base-value); }

  $remValues: ();

  @for $i from 1 through $max {
      $remValues: append($remValues, convertToRem(nth($values, $i), $base-value));
  }

  @return $remValues;
}

