.card[data-prod] {
	[data-product-attribute="set-rectangle"],
	.form-label {
		display: none;
	}

	.swatch-show-product {
		display: inline-block;
		.form-option-variant {
			line-height: $first_value + "px";
		}
	}

	.card-swatches {
		&.hide { opacity: 0; }
	}

