.card-img-container {
    position: relative;
    &:hover {
        .card-image-hover[style] {
            opacity: 1;
        }
    }    
    .card-image {
        height: auto!important;
    }
    .card-image-hover {
        transition: opacity .5s ease-in-out;
        opacity: 0;
        background-position: center center;
        background-size: contain;
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
    }
}

[data-swatches="failed"], [data-swatches="empty"] {
    .card-swatches {
        @if $swatch-hide-empty {
            display: none;
        }
        .loader  {
            display: none;
        }
    }
}

