    width: 100%;
    min-height: $first_value + 'px';
    @if $swatch-grid-layout {
        margin: $swatch-spacing 0;
    } @else {
        margin: ($swatch-spacing - rem-calc($swatch-gap/2)) 0;
    }
	display: flex;
	align-items: center;
    justify-content: center;

