    background-image: url(../img/footer-bg.svg);
    background-repeat: no-repeat;
    background-position: top left;
    background-size: contain;
  }
  

  &.customer-logged-in{
    li.login{display: none !important;}
    li.return{display: list-item !important;}
  }
  
