  .news-letter-section{
    margin-bottom: 40px;
    .header-cta1,  .header-cta1 > p{
      font-size: 20px;
      line-height: 24px;
      color: $color-cta1;
      font-weight: 700;
    }
    form{
      max-width: 410px;
      
    }
