    .ft-links{
      ul{
        padding: 0 10px;
        @include media-breakpoint-up("md") {padding: 0;}
      }
       &:last-child{
         margin-bottom: 40px;
        #sd-simple-text-editable-ea34ef88-033c-42ae-a0bf-88247918c5e9{
          border-bottom: 1px solid $color-cta3;
          @include media-breakpoint-up("md") {border-bottom: 0px solid $color-cta3;}
        }
       } 
      
      
      a[data-toggle="collapse"]{
        .fa-plus::before{
          content:"\f068"
        }
        
        &.collapsed{
          .fa-plus::before{
            content:"\f067"
          }
        }
      }
      ul.collapse{
        margin-bottom: 20px;
      }
      .d-flex{
        border-top: 1px solid $color-cta3;
        justify-content: space-between;
        min-height: 72px;
        align-items: center;
        padding: 0 10px;
