      strong{
        color:$primary;
      }

      @include media-breakpoint-up("md") { 
        .d-flex{border-top: 0px ; min-height: unset; padding: 0;}
        &:last-child{
          .d-flex{
           border-bottom: 0px solid $color-cta3;
         }
        } 

      }
    }

    
  }

  .footer-social {
    a {
      color:$theme-primary;
      font-size:30px;
      transform: all .3s ease-in-out;
      &:hover {
        color:$theme-info;
      }
      &:not(:last-child) {
        margin-right: 1rem;
      }
    }
  }

  .footer-callout {
    font-size: 16px;
    font-weight: bold;
    i {
      font-size: 24px;
    }
  }

  form {
  	position: relative;
    input[type="email"] {
      border: 1px solid $theme-primary;
      border-top-right-radius: 0;
      border-bottom-right-radius: 0;
    }
    button {
      border-top-left-radius: 0 !important;
      border-bottom-left-radius: 0 !important;
      font-weight: bold;
    }
    .js_error {
			position: absolute;
			left: 0;
			bottom: -28px;
			text-transform: uppercase;
			font-size: 14px;
			font-weight: 700;
			color: #FF0000;
			&.already {
				color: $color-cta1;
			}
			&.success {
				color: #008000;
			}
    }
  }
  
	.footer-copyright [data-layout-id],
	.footer-copyright [data-content-region="footer_copyright"] {
		display: inline-block;
	}
}

#consent-manager-update-banner { 
