mojo/custom/header.scss:
#consent-manager{display: none;}
$expandBreakpointMax: 1199px;
$expandBreakpointMin: 1200px;

#main {
// 	a:not(.btn) {
// 		text-decoration: none;
// 		border-bottom: 1px solid;
// 		&:hover {
// 			border-color: transparent;
// 		}
// 	}
}

#sitewide-banner-top {
	text-align: center;
	p { margin: 0; }
	span {
		display: block;
		padding: 8px;
	}
}

#evenflow {
	overflow: hidden;
	position: relative;
}

*:focus { outline: none; }

[data-sub-layout] { padding: 0 !important; }

ol, ul, dl {
	margin-top: 0;
	margin-bottom: 0;
	+ .btn {
		margin-top: 16px;
	}
}
.previewCode {
	display: none !important;
}

a, a:before, a:after, a i, a span, a span:after, a span:before, button, button:before, button:after, button i, button i:before, button i:after, .btn, .btn i, .btn i:before, .btn i:after  {
	transition: color 350ms ease, background-color 350ms ease, box-shadow 350ms ease, border-color 350ms ease, opacity 350ms ease !important;
}

.btn:focus {
	box-shadow: none !important;
}

.form-control:focus {
	box-shadow: none;
}

input[type=number]::-webkit-inner-spin-button, 
input[type=number]::-webkit-outer-spin-button {  
	opacity: 1;
}

header {
  border-bottom: 1px solid $color-cta2;
  // 
  // border-bottom: 1px solid $theme-primary;

  background-color: $color-white;
  // position: sticky;
  // top:0;
  // z-index: 9999;
  .navbar {
    padding-top: 0;
    padding-bottom: 0;
    @media (max-width: $expandBreakpointMax) {
      padding: 0;
    }
    & > .container {
      max-width: none;
      flex-wrap: nowrap;
      @media (max-width: $expandBreakpointMax) {
        // flex-direction: row-reverse;
      }
    }
    .inner-nav{
      min-width: 160px;
      justify-content: space-between;
      position: relative;
      
    }

    .navbar-brand {
      min-height: 60px;
      display: flex;
      align-items: center;
      justify-content: center;
      max-width: 140px;
      @media (min-width: $expandBreakpointMin) {
        margin-left: 0;
        flex-shrink: 1;
        margin-bottom: 0;
        min-width: 216px;
      }
    }

    .navbar-collapse > .container {
      padding: 0;
      flex-direction: column-reverse;
      align-items: start;
      @media (max-width: $expandBreakpointMax) {
        max-width: none;
      }
      @media (min-width: $expandBreakpointMin) {
        flex-direction: row;
      }
    }
    .cart-link{
      position: relative;
    }
    .cart-count {
      position: absolute;
      top: 5px;
      right: 8px;
      display: flex;
      span.cart-quantity {
        position: absolute;
        left: 0px;
        top: 50%;
        transform: translateY(-50%);
        right:0;
        margin: auto;
        color: #fff;
        font-size: 8px;
        text-align: center;
        font-weight: 700;
        height: 14px;
      }

      @media (min-width: $expandBreakpointMin){ 
        top: 18px;
        right: 12px;
        display: -ms-flexbox;
        display: flex;
        min-width: 14px;
        min-height: 14px;
        background-color:$color-cta1;
        clip-path: polygon(50% 0, 100% 20%, 100% 80%, 50% 100%, 0 80%, 0 20%);

        span.cart-quantity {
          font-size: 8px;
          text-align: center;
          font-weight: 700; 
          line-height: 1;
          display: flex;
          align-items: center;
          justify-content: center;
          width: 100%;
          
        }
      }
    }
    
  }

  .nav-wrapper {
    width: 100%;
    @media (min-width: $expandBreakpointMin) {
      width: 100%;
    }
  }
}

// ---------------------------------------------------------
// Shared Navigation
// ---------------------------------------------------------



.nav-primary,
.nav-secondary {
  /*.floating-menu-nav {
    @media (min-width: 1200px) {
      position: absolute;
      right: 0;
      top: 10px;
    }
  }*/
  ul.nav {
    .nav-item {
      position: initial;
      width: 100%;
      -webkit-box-flex:1;-ms-flex-positive:1;flex-grow:1;
      &:last-child{
        flex-grow: 0;
        -webkit-box-flex:0;-ms-flex-positive:0;flex-grow:0;
      }

      // @media (min-width: $expandBreakpointMin) {
      //   display: inline-flex;
      // }
      &.has-menu {
        &:hover > .nav-menu {
          display: block;
        }
      }
      @media (min-width: $expandBreakpointMin) {
        width: auto;
      }
      .nav-link {
        font-size: 16px;
        display: flex;
        align-items: center;
        border-radius: 0.25rem;
        transition: all 0.2s ease-in-out;
        color: $dark;
        border-top: 0px solid #e2e2e2;
        border-radius: 0;
        line-height: 50px;
        font-weight: bold;
        padding: 0;
        @media (min-width: $expandBreakpointMin) {
          padding: 0 1rem;
          font-size: 18px;
          padding: 0.45rem 0.15rem 0;
          margin: 2px 1rem ;
          border-top: none;
          line-height: 35px;
        }
        &:hover {
          color: $primary;
        }

        span.more {
          margin-left: auto;
          padding: 0;
          width: 40px;
          text-align: center;
          // margin-right: -1.5rem;
          display: block;
          border-left: 0px solid #e2e2e2;
          i{
            font-size: 16px !important;
          }
          @media (min-width: $expandBreakpointMin) {
            border-left: none;
            margin: 0;
            padding: 0 0 0 0.8rem;
            display: none;
            i:before {              
              content: "\f105";
            }
          }
          &.active {
            i:before {
              content: "\f105";
            }
          }
        }


      }
      .nav-menu {
        display: none;
        position: absolute;
        background: #dee2e6;
        min-width: 300px;
        box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.2);
        border-radius: 0.25rem;
        z-index: 1;
        .nav-link {
          padding-top: 1.5rem;
          padding-bottom: 1.5rem;
        }
      }

      .nav-submenu {
        display: none;
      }
    }
  }
}

.nav-primary{
  
  ul.nav.level-0 {
    .nav-item {
      .nav-link {
        @media (min-width: $expandBreakpointMin) {
          margin: 2px 1rem 20px 0;
          width: -webkit-max-content;width: -moz-max-content;width: max-content;
        }
      }
    }
  }
}

.nav-primary,
.nav-secondary {
  width: 100%;
  ul,
  li,
  a {
    width: 100%;
    @media (min-width: $expandBreakpointMin) {
      width: auto;
    }
  }
}

// ---------------------------------------------------------
// Primary Navigation
// ---------------------------------------------------------
.nav-primary {
  a.menu-bold.nav-link.shop-all-link {
    color: $color-cta1 !important;
    font-size: 110% !important;
    text-transform: capitalize;
}


  ul.nav   {
    margin: 0;
    flex-direction: column;

    @media (min-width: $expandBreakpointMin) {
      flex-direction: row;
      justify-content: space-between;

    }
    
    > li.nav-item {
      a.nav-link {
        font-weight: normal;
        color: $primary;
        font-size: 15px;
        line-height: 24px;
      }
      
      @media (min-width: $expandBreakpointMin) {
        &.has-submenu.is-open {
          > .nav-link{
            border-bottom: 1px solid $primary;
          }
          div.nav-submenu {
            display: block !important;
          }
        }
      }

    }
    div.nav-submenu {
      // TESTING
      // display: block !important;
      // TESTING

      border-left: 0px solid $gray;

      width: 100%;
      margin: 0 auto;

      // position: absolute;
      // background: $color-white;
      // height: 100vh;
      // top: 0;
      // transform: translatex(100%);
      // transition: all 0.5s ease-in-out;
      
      // &.active{
      //   transform: translatex(0px);
      // }

      // Desktop Dropdown Menu (Mega Menu)
      @media (min-width: $expandBreakpointMin) {
        border-left: none;
        position: absolute;
        left: 0;
        right: 0;
        top: 100%;
        width: 100%;
        z-index: 10;
        background: $color-white;

        box-shadow: 0 4px 10px  rgba(0, 0, 0, 0.2);

        padding: 1rem;
        li.nav-item {
          a.nav-link {
            padding: 0;
          }
        }
        >div{
          max-width: 1600px;
          margin: auto;
          padding: 0 1rem;
          margin-bottom: 30px;
          strong{
            font-size: 14px;
            line-height: 50px;
            color: $color-black;
          }
          ul{
            list-style: none;
            padding-left: 0;
            li{
              a{
                font-size: 14px;
                line-height: 30px;
                text-decoration: none;
                color: $secondary;
              }
            }
          }
         
        }
        // Headings
        > ul > li.nav-item > a.nav-link {
          border-bottom: 1px solid #ddd;
          font-size: 18px;
          margin-right: 2rem;
          color: $primary;
          &:hover {
            color: darken($theme-primary, 10%);
          }
          & + .nav-submenu li.nav-item a.nav-link {
            padding: 0.25rem 0;
          }
        }

        span.more {
          display: none !important;
        }

        ul.nav-submenu {
          display: block !important;
        }

      }

       
        
        
       
    }
  }
}

// ---------------------------------------------------------
// Secondary Navigation
// ---------------------------------------------------------
.nav-secondary {
  ul.nav {
    margin: 0;
    flex-direction: column;
    @media (min-width: $expandBreakpointMin) {
      margin: 0;
      flex-direction: row;
    }
    li.nav-item {
      &:not(.nav-item-form) {
        display: none !important;
      }
      @media (min-width: $expandBreakpointMin) {
        &:not(.nav-item-form) {
          display: block !important;
        }
        a.nav-link {
          padding: 1rem 0.25rem;
        }
      }
    }
  }

  form {
    margin: 0 auto 1rem auto;
    padding: 0;
    @media (min-width: $expandBreakpointMin) {
      margin: 0;
      margin-right: 1rem;
    }
    .form-control {
      &:focus,
      &:focus:hover {
        outline: none;
      }
      @media (min-width: $expandBreakpointMin) {
        min-width: 300px;
      }
      &::placeholder {
        color: $theme-dark;
        font-size: 14px;
      }
    }
    button {
      text-transform: uppercase;
    }
  }
}

// ---------------------------------------------------------
// Navbar Toggler
// ---------------------------------------------------------
.navbar-toggler {
  box-sizing: border-box;
  animation: 0.3s linear 0s normal none 1 cd-bounce-in;
  transition: all 0s ease 0s;
  position: relative;
  outline-color: #e2e2e2 !important;
  display: none;
  span {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%);
    width: 50%;
    height: 2px;
    background:$secondary;
    transition: background 0s ease 0s;
  }
  span:before,
  span:after {
    background:$secondary;
    content: "";
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    box-sizing: border-box;
    backface-visibility: hidden;
    transition: transform 0.3s ease 0s, background 0s ease 0s;
  }
  span:before {
    top: 0;
    transform: rotate(45deg);
  }
  span:after {
    bottom: 0;
    transform: rotate(-45deg);
  }
  .navbar-toggler-icon {
    background-image: none !important;
  }
}
.navbar-toggler[aria-expanded="false"] {
  span {
    background: $secondary;
  }
  span:before {
    top: -7px;
    transform: rotate(0);
  }
  span:after {
    bottom: -7px;
    transform: rotate(0);
  }
}
.navbar-button {
  width: 40px;
  height: 52.5px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 !important;
  border: none !important;
  // margin: 0 0.5rem;
  @media (max-width: $expandBreakpointMax) {
    display: block;
  }
}
.navbar-button i[class*="fa-"] {
  font-size: 14px;
  line-height: 24px;
  color: $secondary;
  padding: 0 0.45rem;
}
.navbar-button--addon {
  // width: unset;
  // height: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  text-decoration: none !important;
  
  i {
    font-size: 14px;
    color: $secondary;
    min-width: 40px;
    text-align: center;
    @media (min-width: $expandBreakpointMax) {
      font-size: 18px;
    }
  }
}

