mojo/custom/widgets.scss:
@mixin rowBackgroundColor($class, $color) {
  [data-layout-name*="#{$class}"] {
    position: relative;
    &:before,
    &:after {
      content: "";
      position: absolute;
      background: stencilColor($color);
      width: 100vw;
      top: 0;
      height: 100%;
      z-index: -1;
    }
    &:before {
      left: -25vw;
    }
    &:after {
      right: -25vw;
    }
  }
}
@mixin rowTextColor($class, $color) {
  [data-layout-name*="#{$class}"] {
    color: stencilColor($color);
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    h1 span,
    h2 span,
    h3 span,
    h4 span,
    h5 span,
    h6 span {
      color: stencilColor($color);
    }
  }
}

// You can add any of the following "text slugs" to the name of any layout to change how it behaves.  Multiple slugs may be added, separated by a space.

@include rowBackgroundColor("bg-primary", "color-primary");
@include rowBackgroundColor("bg-secondary", "color-secondary");
@include rowBackgroundColor("bg-light", "color-greyLightest");
@include rowBackgroundColor("bg-dark", "color-grey");
@include rowBackgroundColor("bg-cta1", "color-success");
@include rowBackgroundColor("bg-cta2", "color-info");
@include rowBackgroundColor("bg-cta3", "color-error");

@include rowTextColor("text-primary", "color-primary");
@include rowTextColor("text-secondary", "color-secondary");
@include rowTextColor("text-light", "color-greyLightest");
@include rowTextColor("text-dark", "color-grey");
@include rowTextColor("text-cta1", "color-success");
@include rowTextColor("text-cta2", "color-info");
@include rowTextColor("text-cta3", "color-error");

.max-1440,
[data-layout-name*="max-1440"] {
  max-width: 1440px;
  margin: 0 auto;
}
.max-1200,
[data-layout-name*="max-1200"] {
  max-width: 1200px;
  margin: 0 auto;
}
.max-800,
[data-layout-name*="max-800"] {
  max-width: 800px;
  margin: 0 auto;
}

[data-layout-name*="Layout"] {
  height: auto;
  & > [data-sub-layout] {
    height: auto;
  }
}

// add extra space to the top of header_top--global for the text editor - tinymice gets hidden
[data-content-region="header_top--global"] {
  .mce-content-body.mce-edit-focus {
    margin-top: 90px;
  }
}

@mixin categoryBlockGrid($mobile, $desktop, $margin) {
  [data-type="common/category-blocks"][data-columns="#{$desktop}"] .category-block-list .category-block {
    width: calc(100% / #{$mobile} - #{$margin}) !important;
    @include media-breakpoint-up("md") {
      width: calc(100% / #{$desktop} - #{$margin}) !important;
    }
  }
}
@include categoryBlockGrid(1, 1, 16px);
@include categoryBlockGrid(2, 2, 16px);
@include categoryBlockGrid(2, 3, 16px);
@include categoryBlockGrid(2, 4, 16px);
@include categoryBlockGrid(2, 5, 16px);

[data-type="common/category-blocks"] {
  &[data-layout="grid"] .category-block-list {
    display: flex;
    flex-wrap: wrap;
    .category-block {
      border: 1px solid #8c8c8c;
      box-shadow: none;
      margin: 8px;
      span {
        font-size: 16px;
        padding: 20px 5px;
      }
      img {
        height: 150px;
      }
    }
  }
  
  &[data-layout="grid"] {
    display: grid;
    grid-template-rows: 1fr;
    gap: 0px 0px;
    &[data-columns] {
      grid-template-columns: 1fr;
    }
    @media (min-width: 768px) {
      &[data-columns] {
        grid-template-columns: 1fr 1fr;
      }
    }
    @media (min-width: 1200px) {
      &[data-columns="2"] {
        grid-template-columns: 1fr 1fr;
      }
      &[data-columns="3"] {
        grid-template-columns: 1fr 1fr 1fr;
      }
      &[data-columns="4"] {
        grid-template-columns: 1fr 1fr 1fr 1fr;
      }
      &[data-columns="5"] {
        grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
      }
    }
  }
}

.searchList--products {
  padding: 1rem 0;
  a {
    text-decoration: none;
  }
  a:hover {
    & + div .search-title {
      color: $theme-primary;
    }
  }
  figure {
    img {
      display: block;
      margin: auto;
      max-height: 200px;
      object-fit: contain;
      object-position: center center;
    }
  }
  .searchList-item {
    padding-bottom: 2rem;
    text-align: center;
    @media (min-width: 768px) {
      padding: 0 1.65rem 2rem;
    }
  }
  .search-title {
    color: $theme-dark;
    font-weight: bold;
    line-height: 1.2;
    margin-bottom: 5px;
    cursor: pointer;
    &:hover {
      color: $theme-primary;
    }
  }
  .search-brand {
    margin-bottom: 5px;
    font-size: 12px;
    line-height: 2.33;
    text-align: center;
    color: #333333;
    a {
      color: $theme-dark;
      &:hover {
        color: $theme-primary;
      }
    }
  }
  .search-price {
    font-weight: bold;
    color: $theme-primary;
  }
}

.searchList--pages {
  .searchList-item {
    a {
      margin-bottom: 5px;
      display: flex;
      align-items: stretch;
      flex-direction: row;
      text-decoration: none;
      border-radius: 5px;
      width: 100%;
      background: $theme-light;
      padding: 10px;
      display: flex;
      // flex-direction: column;
      align-items: start;
      justify-content: center;
      min-height: 60px;
      &:hover {
        background: darken($theme-light, 8%);
        text-decoration: none;
      }
    }
    .search-title {
      color: $primary;
      font-weight: bold;
    }
    .search-desc {
      margin: 2px 0;
      font-size: 14px;
      line-height: 16px;
      color: $theme-dark;
    }
  }
}

.searchBarWidget {
  form {
    max-width: 640px;
    margin: 0 auto;
    position: relative;
  }
  [class*="searchList--type"] {
    display: none;
    h4 {
      display: none;
    }
  }
  legend + div {
    width: 100%;
  }
  input.form-control {
    width: 100%;
    height: 50px;
  }
  .btnIconSearch {
    display: block !important;
    position: absolute;
    border: none;
    background: none;
    top: 5px;
    right: 5px;
    height: 40px;
    line-height: 40px;
    width: 40px;
  }
  .dropdown--quickSearch {
    position: relative;
    margin-top: 8px;
    box-shadow: 0 0 10px -1px rgba(0, 0, 0, 0.3);
    &.is-open {
      top: 0 !important;
    }
  }
  [type="submit"] {
    display: none;
  }
  &[data-type="all"] .searchList--type--webpages,
  &[data-type="all"] .searchList--type--products {
    display: block;
  }
  &[data-type="webpages"] .searchList--type--webpages {
    display: block;
  }
  &[data-type="products"] .searchList--type--products {
    display: block;
  }
}

// Web Page Styling
.page-container {
  @media (max-width: 1200px) {
    max-width: none;
  }
  @media (max-width: 767px) {
    .page-sidebar {
      margin-bottom: 1.5rem;
    }
  }
}

// Mobile Page Builder Overrides
@media (max-width: 700px) {
  [id*="sd-image-"] {
    margin-bottom: 1rem !important;
  }
  [data-sub-layout] {
    padding-left: 0 !important;
    padding-right: 0 !important;
  }
}

// Testimonials Carousel
.testimonialsCarousel {
  position: relative;
  padding-top: 5rem;
  padding-bottom: 5rem;
  @extend .container, .mx-auto;

  &:before {
    content: "“”";
    position: relative;
    display: block;
    margin: 0 auto;
    font-size: 100px;
    font-weight: bold;
    line-height: 0.4;
    text-align: center;
    color: #ffffff;
  }
  .testimonialsCarousel-slide {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
  }
  .testimonialsCarousel-image-wrapper {
    margin-top: 1rem;
    display: flex;
    align-items: center;
    img {
      width: 121px;
      border-radius: 100%;
      border: 2px solid #fff;
    }
    .testimonialsCarousel-image-info {
      margin-left: 1rem;
      color: #fff;
      text-align: left;
      font-weight: bold;
      line-height: 24px;
      span {
        display: block;
      }
    }
  }
  .testimonialsCarousel-content {
    .testimonialsCarousel-description {
      color: #fff;
      font-size: 24px;
      text-align: center;
      max-width: 888px;
      padding-left: 30px;
      padding-right: 30px;
      @media (max-width: 992px) {
        font-size: 20px;
      }
    }
  }
  .owl-nav {
    button.owl-next,
    button.owl-prev {
      background: none !important;
      height: auto;
      padding-left: 15px !important;
      padding-right: 15px !important;
      padding-bottom: 10px !important;
      span {
        color: #fff;
      }
    }
  }
}

// Logo Carousel
.logoCarousel {
  position: relative;
  padding-top: 3rem;
  padding-bottom: 5rem;
  @extend .container, .mx-auto;

  .logoCarousel-slide {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    .logoCarousel-image-wrapper {
      img {
      }
    }
  }
  .owl-nav {
    button.owl-next,
    button.owl-prev {
      background: none !important;
      height: auto;
      padding-left: 15px !important;
      padding-right: 15px !important;
      padding-bottom: 10px !important;
      span {
        color: #000;
      }
    }
  }
}

// Card Grid
.cardGrid {
  padding: 0 15px;
  .row {
    &::before, &::after{content: unset;}
    display: -ms-grid;
    display: grid;
    -ms-grid-columns: 1fr 10px 1fr;
    grid-template-columns: repeat(2, 1fr);
    grid-gap:10px;
    // align-items: stretch;
    // justify-content: space-between; 
  }
  .col.mb-2{
    // flex-basis: 100%;
    // max-width: 100%;
    max-height: 120px;
    padding: 0px;
    // margin: 5px 0 !important;
    &:last-child{
      display: none;
    }
  }
  .cardGrid-item {
    background: #fff;
    text-align: center;
    // padding: 33px 20px 33px 20px;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    // border: 1px solid $color-cta3;
    min-height: 100px;
    background-repeat: no-repeat;
    i {
      // font-size: 44px;
      color: $primary;
    }

    a {
      text-decoration: none;
    }

    &[style*="background-image"] {
      padding: 0;
      transition: all 0.25s ease-in-out;
      border: 1px solid $color-cta2;
      a:not(.btn) {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        padding: 33px 20px 33px 20px;
        height: 100%;
        width: 100%;
        .cardGrid-content {
          margin-top: 0;
        }
        .cardGrid-title {
          font-weight: normal;
          color: #fff;
          text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
        }
        i {
          color: #fff;
          text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
        }
      } 
    }

    @include media-breakpoint-up("md") {
      padding: unset;
    }
  }
  .cardGrid-content {
    margin-top: 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
  }
  .cardGrid-title {
    margin: 21px 0 0;
    font-size: 16px;
    font-weight: bold;
    line-height: 1.5;
    color: $primary;
  }

  a:empty {
    display: none;
  }
  .cardGrid-item{background-size: cover !important;}
  @include media-breakpoint-up("md") {
    .cardGrid-item{background-size: cover !important;}
    .row {
      -ms-grid-columns: 1fr 10px 1fr 10px 1fr;
      grid-template-columns: repeat(3, 1fr);
      // display: flex;
      // align-items: stretch;
      // justify-content: flex-start;  
    }
    .col.mb-2{
      // flex-basis: calc(100% / 3);
      // max-width: calc(100% / 3);
      max-height: 120px;
      // padding: 0px 15px;
      margin: 5px 0 !important;
      // &:first-child{padding-left: 0;}
      // &:nth-child(4){padding-left: 0;}
      // &:last-child{padding-right: 0;}
    }
    .cardGrid-item{
      min-height: 120px;
      min-width: 180px;
    }

  }
  @include media-breakpoint-up("lg") {
    .row{
      -ms-grid-columns: 1fr 10px 1fr 10px 1fr 10px 1fr;
      grid-template-columns: repeat(4, 1fr);
    }
    .cardGrid-item{background-size: cover !important;}
    .col.mb-2{
      // flex-basis: calc(100% / 4);
      // max-width: calc(100% / 4);
      // &:first-child{padding-left: 0;}
      // &:nth-child(5){padding-left: 0;}
    }

  }
  @include media-breakpoint-up("xl") {
    
    .row {
      -ms-grid-columns: 1fr 30px 1fr 30px 1fr 30px 1fr 30px 1fr;
      grid-template-columns: repeat(5, 1fr);
      grid-gap:30px;
      
      // display: flex;
      // align-items: stretch;
      // justify-content: space-between; 
      // padding: 0 15px;
    }
    .col.mb-2{
      // flex-basis: calc(100% / 5);
      // max-width: calc(100% / 5);
      // &:first-child{padding-left: 0;}
      // &:nth-child(4){padding-left: 15px;}
      // &:nth-child(5){padding-left: 15px;}
      // &:last-child{padding-right: 0;}
      &:last-child{
        display: block;
      }
    }
  }
}

// Newsletter Form
.newsletterForm {
  input.form-input {
    width: 320px;
    height: 45px;
    margin: 3px 10px 7px 68px;
    padding: 10px;
    border-radius: 4px;
    background-color: #ffffff;
    border: 1px solid #ffffff;
  }

  .button {
    width: 106px;
    height: 42px;
    border-radius: 4px;
    border: solid 1px #ffffff;
    background-color: $theme-primary;
    color: #fff;
    margin-left: 0;
  }
}

// Product Carousel
.productCarousel {
  .owl-stage {
    display: flex;
    align-items: stretch;
    .owl-item {
      .productCarousel-slide {
        height: 100%;
      }
    }
  }
  .owl-nav button span {
    padding: 0 !important;
  }
}

mojo/custom/widgets-client.scss:
.skip_carousel {
	display: block;
	width: 140px;
	margin: 0 auto 24px;
	&:not(:focus) {
		position: absolute;
		padding: 0;
		margin: -1px;
		overflow: hidden;
		clip: rect(0, 0, 0, 0);
		white-space: nowrap;
		border: 0;
	}
}

// --------------------------------------------------
// Image List
// --------------------------------------------------

.widget-img-list {
	a {
		text-decoration: none;
		display: block;
		&:hover {
			@media(min-width: 992px) {
				.widget-img-list-content .widget-img-list-button span {
					color: $primary !important;
					background-color: #fff !important;
				}
			}
			@media(max-width: 992px) {
				.widget-img-list-content .widget-img-list-button span {
					color: #fff !important;
					background-color: $primary !important;
				}
			}
		}
	}

  .widget-img-list-slide {
    position: relative;
    padding: 0;

    .widget-img-list-bg {
      &.bg-preserve-mobile-right {
        background-position: right;
      }
      &.bg-preserve-mobile-left {
        background-position: left;
      }
      &.bg-preserve-mobile-center {
        background-position: center;
      }
      @media (min-width: 992px) {
        &.bg-preserve-right {
          background-position: right;
        }
        &.bg-preserve-left {
          background-position: left;
        }
        &.bg-preserve-center {
          background-position: center;
        }
      }

      &.bg-preserve-mobile-right img {
        object-position: right;
      }
      &.bg-preserve-mobile-left img {
        object-position: left;
      }
      &.bg-preserve-mobile-center img {
        object-position: center;
      }
      @media (min-width: 992px) {
        &.bg-preserve-right img {
          object-position: right;
        }
        &.bg-preserve-left img {
          object-position: left;
        }
        &.bg-preserve-center img {
          object-position: center;
        }
      }
    }
    .widget-img-list-banner {
      text-align: center;
      padding: 5px 10px;
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
    }
    .widget-img-list-content {
      align-self: center;
      margin-left: auto;
      margin-right: auto;
      padding: 2rem 15px;
      flex-shrink: 0;
      @media (min-width: 992px) {
        max-width:50%;
        padding: 2rem 40px;
      }

      @media (max-width: 414px) {
        padding: 16px;
      }

      .widget-img-list-headline {
        margin-bottom: 10px;
//         h1 {
//         	font-size: 40px;
//         	text-align: left;
//         	line-height: 40px;
//         	margin-bottom: 8px;
//         	color: #333F48;
//         }
      }
      .widget-img-list-button {
        margin: 15px auto 0;
        display: block;
        a,
        span.link-btn {
          display: block;
          margin: 0 auto;
          border: 1px solid #777;
          padding: 10px 20px;
          display: block;
          text-decoration: none;
          transition: all 0.3s ease-in-out;
          text-align: center;
        }
        &.auto {
          max-width: 200px;
        }
      }
      &.text-align-center {
        text-align: center;
      }
      &.text-align-left {
        text-align: center;
        .widget-img-list-button {
          margin-right: auto;
          margin-left: auto;
        }
        @media (min-width: 992px) {
          text-align: left;
          .widget-img-list-button {
            margin-left: 0;
            margin-right: auto;
          }
        }
      }
      &.text-align-right {
        text-align: center;
        .widget-img-list-button {
          margin-right: auto;
          margin-left: auto;
        }
        @media (min-width: 992px) {
          text-align: right;
          .widget-img-list-button {
            margin-left: auto;
            margin-right: 0;
          }
        }
      }
      &.category-banner {
      	@media (min-width: 992px) {
      		padding-right: 50%;
				}
      	@media (min-width: 1200px) {
      		padding-right: 50%;
				}
				@media (max-width: 991px) {
					padding-bottom: 0;
				}
				@media (min-width: 1600px) {
					padding-right: 800px;
				}
      }
    }

    &.mobile-bg-white {
      @media (max-width: 991px) {
        &:not(.new-layout) {
        	background-color: #fff !important;
				}
        .widget-img-list-content {
					* { color: #333 !important; }
          a,
          span.link-btn {
          	border-color: #333 !important;
          	&:hover {
          		background-color: #333;
          		color: #fff !important;
          	}
          }
        }
      }
    }

    &.text-position-center {
      .widget-img-list-content {
        @media (min-width: 992px) {
          position: absolute;
          top: 50%;
          left: 0;
          right: 0;
          transform: translateY(-50%);
        }
      }
    }

    &.text-position-left,
    &.text-position-right {
      display: flex;
      flex-direction: column;
      .widget-img-list-bg img {
        width: 100%;
        object-fit: cover;
      }
      @media (min-width: 992px) {
        .widget-img-list-bg img {
          width: auto;
        }
        .widget-img-list-content {
          padding-top: initial;
          padding-bottom: initial;
          min-width: 45%;
        }
      }
      @media (max-width: 991px) {
        .widget-img-list-content {
          margin-right: 0;
          margin-left: 0;
          width: 100%;
        }
      }
    }

    &.text-position-right {
      flex-direction: row;
      @media (max-width: 991px) {
        flex-direction: column;
      }
    }
    &.text-position-left {
      flex-direction: row-reverse;
      @media (max-width: 991px) {
        flex-direction: column;
      }
    }

		&.new-layout,
		&.category-banner {
			.widget-img-list-content {
				flex: 0 0 50%;
			}
			.widget-img-list-bg {
				display: flex;
				justify-content: center;
				img {
					object-fit: revert;
					min-height: 0;
					width: auto;
					align-self: center;
				}
			}
			@media (max-width: 991px) {
				&.mobile-bg-white .widget-img-list-content {
					background: #fff !important;
				}
			}
		}
		&.category-banner {
			.widget-img-list-bg {
				background-size: contain;

				@media (max-width: 1199px) and (min-width: 992px) {
					margin-right: -200px;
				}
				@media (max-width: 960px) {
					min-height: 240px;
					background-size: cover;
					background-position: center center !important;
				}
// 				@media (max-width: 600px) {
// 					background-position: calc(50% - 48px) 50% !important;
// 				}
			}
		}

  }

  &.grid-layout {
    display: grid;
    grid-template-columns: repeat(2, minmax(0, 1fr));
    gap: 30px;
    align-items: stretch;
    @media (max-width: 767px) {
      display: flex;
      flex-wrap: wrap;
      flex-direction: column;
      gap: 0;
      .widget-img-list-slide {
        max-width: 100%;
      }
    }
    @media (min-width: 767px) {
      .widget-img-list-slide {
      }
    }
  }

  .owl-nav {
    margin: 0;
    button {
      margin: 0 !important;
      transition: all 0.3s ease-in;
      border-radius: 0;
      &:hover {
        background: rgba(0, 0, 0, 0.25) !important;
        span {
          color: #fff;
        }
      }
    }
  }
}

// --------------------------------------------------
// Product Carousel
// --------------------------------------------------
.widget-product-carousel {
  .widget-product-carousel-item {
    position: relative;
    a {
      color: initial;
      text-decoration: none;
      transition: color 350ms ease;
      display: block;
			overflow: hidden;
      &:hover,
      &.hovered {
      	color: $secondary;
      	@media(min-width: 769px) {
					img { transform: scale(1.03); }
      	}
      }
    }
    img {
      max-height: 300px;
      transition: transform 350ms ease;
      object-fit: contain;
      object-position: center;
    }
    .widget-product-carousel-badges {
      position: absolute;
      top: 0;
      left: 0;
      display: flex;
      [class*="badge-"] {
        margin-right: 4px;
        display: block;
        background: #ffffff;
        border: 1px solid #333f48;
        padding: 0 6px;
      }
      .badge-New {
        border-color: #002554;
        color: #002554;
      }
      .badge-Sale {
        border-color: #db3433;
        color: #db3433;
      }
      .badge-Bestseller {
        border-color: #165c7d;
        color: #165c7d;
      }
    }
    .widget-product-carousel-reviews {
      margin-bottom: 3px;
      letter-spacing: -2px;
      i.fas {
        color: #f0b323;
      }
    }
    .widget-product-carousel-content {
      margin-top: 10px;
      .name {
        line-height: 1.2;
      }
      .price {
        margin-top: 10px;
        font-size: 14px;
        color: #333f48;
        &.on-sale {
          .price-sale {
            color: #db3433;
            padding-right: 5px;
          }
          .price-base {
            text-decoration: line-through;
          }
        }
      }
    }
  }
}

// --------------------------------------------------
// Content list
// --------------------------------------------------
.widget-content-list {
  .widget-content-list-item {
    margin-bottom: 15px;
    .widget-content-list-image {
			display: block;
			overflow: hidden;    
      img {
        max-height: 300px;
        object-fit: contain;
        object-fit: center;
				transition: transform 350ms ease;
      }
    }
    .widget-content-list-content {
      margin-top: 16px;
      padding: 0 10px;
    }
		.widget-content-headline {
			a {
				text-decoration: none;
			}
		}
		@media(min-width: 769px) {
			a.hovered img {
				transform: scale(1.04);
			}
		}
    .widget-content-list-link {
      margin-top: 10px;
      a {
        color: $primary;
        text-decoration: none;
        border-bottom: 1px solid;
        transition: border-color 350ms ease;
        &:hover,
        &.hovered {
        	border-color: transparent;
        }
      }
      a:not(:last-child) {
        margin-right: 20px;
      }
    }

    &[style*="left"] {
      .widget-content-list-content {
        margin-left: 30px;
        padding-right: 10px;
      }
    }
    &[style*="right"] {
      .widget-content-list-content {
        margin-right: 30px;
        padding-left: 10px;
      }
    }
  }

//   .owl-nav {
