mojo/custom/product-display.scss:
.productView {
  @include grid-row($behavior: "nest");
  margin-left: -(spacing("base"));
  margin-right: -(spacing("base"));

  .modal & {
    padding-bottom: 0;
    padding-top: 0;
  }
}

.productView--quickView {
  .socialLinks-item--print {
    display: none;
  }
}

.productView-images {
  position: relative;
  @include grid-column(12);
  padding-left: spacing("base");
  padding-right: spacing("base");

  @include media-breakpoint-up("md") {
    width: grid-calc(6, $total-columns);
  }
  @include media-breakpoint-up("lg") {
    width: grid-calc(6, $total-columns);
  }
}

.productView-details {
  @include grid-column(12);
  padding-left: spacing("base");
  padding-right: spacing("base");

  @include media-breakpoint-up("md") {
    clear: right;
    float: right;
    width: grid-calc(6, $total-columns);
  }
  @include media-breakpoint-up("lg") {
    width: grid-calc(6, $total-columns);
  }
}

.productView-description {
  @include grid-column(12);
  
  @include media-breakpoint-up("md") {
    padding-left: 0;
    padding-right: 0;
  }

  .productView-title {
    border-bottom: container("border");
    margin-bottom: spacing("base");
    padding-left: spacing("base");
    padding-right: spacing("base");
  }

  .productView-description {
    @include media-breakpoint-up("md") {
      clear: both;
      float: none;
      width: 100%;
    }
  }
}

.productView-images {
  margin-bottom: spacing("base");
}

.productView-image {
  align-items: center;
  display: flex;
  justify-content: center;
  margin: auto;
  position: relative;
  // border: 1px solid #C9CAC6;

  @include media-breakpoint-up("md") {
    min-height: 366px;
    min-width: inherit;
  }
}

.productView-img-container {
  position: relative;
  margin: auto;
  max-width: get-width(stencilString("product_size"));
  width: 100%;

  img {
    @include lazy-loaded-img;
    max-height: 100%;
    width: 100%;
    object-fit: contain;
    /* Object-fit polyfill */
    font-family: "object-fit: contain;";
  }

  @include lazy-loaded-padding("product_size");
  &:after{
    padding-bottom: 100%;
  }
}
.productView-thumbnails.mobile-carosel{
  list-style: none;
  margin: 0;
  padding: 0;
  
  &::before{
    display: none;
  }

}
.productView-thumbnails:not(.mobile-carosel) {
  list-style: none;
  margin: 0;
  padding: 0;
  @include grid-row($behavior: "nest");

  &::before{
    display: none;
  }
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 20px;
  padding: 15px;

  img {
    @include lazy-loaded-img;
    object-fit: contain;
    object-fit: contain;
    font-family: "object-fit: contain;";
    position: relative;
    height: 100%;
    width: 100%;
    max-width: 350px;
    max-height: 400px;
  }
}

.productView-thumbnail {
  text-align: center;
  border: 1px solid $color-cta3;  
}

.productView-thumbnail-link {
  // border: container("border");
  // display: inline-flex;
  // position: relative;
  // width: 100%;
  // box-sizing: content-box;
  // justify-content: center;

  &:hover,
  &.is-active {
    border-color: color("greys", "darker");
  }
}

// Details
// -----------------------------------------------------------------------------

.productView-title {
  line-height: 40px;
  font-weight: 400;
  word-break: break-word;
  @include media-breakpoint-up("lg") {
    line-height: 48px;
    word-break: unset;
  }
}

.productView-details {
  margin-bottom: 15px;
  top: 40px;
  @include media-breakpoint-up("md") {
    position: sticky;
    top: 40px;
  }
  @include media-breakpoint-up("xl") {
    top: 40px;
  }

}

// Details - Product details
// -----------------------------------------------------------------------------

.productView-info {
  @include clearfix;
  margin-top: spacing("half");

  @include media-breakpoint-up("small") {
    margin-top: 0;
  }

  > :first-child {
    @include media-breakpoint-up("small") {
      margin-top: spacing("single");
    }
  }

  > :last-child {
    margin-bottom: 0;
  }
}

.productView-info-name,
.productView-info-value {
  float: left;

  @include media-breakpoint-up("small") {
    float: none;
  }
}

.productView-info-name {
  clear: both;
  margin-bottom: spacing("eighth");
  margin-right: spacing("quarter");

  @include media-breakpoint-up("small") {
    font-family: fontFamily("headingSans");
    font-size: fontSize("smallest");
    font-weight: fontWeight("normal");
  }
}

.productView-info-value {
  margin-bottom: spacing("quarter");

  @include media-breakpoint-up("small") {
    margin-bottom: spacing("base");
  }
}

// Details - Product options
// -----------------------------------------------------------------------------
//
// 1. Align the product options list item radios vertically.
// 2. Resetting font due to inline-block whitespace issue.
// 3. File upload button does not properly display in IE if font-size is 0
//
// -----------------------------------------------------------------------------

.productView-options {
  @include clearfix;
  margin-bottom: spacing("single");
  text-align: center;

  @include media-breakpoint-up("small") {
    text-align: left;
  }

  .form-input[type="number"] {
    @include media-breakpoint-up("small") {
      width: grid-calc(2, $total-columns);
    }
  }

  .form {
    display: inline;

    @media (max-width: 1200px) {
      display: block;

    }
  }

  input[type="file"] {
    font-size: fontSize("smallest"); // 3
  }

  .form-field {
    // font-size: 0; // 2
    margin-bottom: 1rem;

    .form-label {
      text-transform: capitalize;
      display: flex;
      justify-content: flex-start;
      max-width: 280px;
      font-weight: normal;
      margin-bottom: 1rem;
      color: $primary;
      span[data-option-value]{color:$secondary; margin-left: 4px;}
    }

    // scss-lint:disable SelectorDepth, NestingDepth
    > .form-checkbox + .form-label,
    > .form-radio + .form-label {
      display: block;
      margin-left: spacing("single");
      width: auto;

      @include media-breakpoint-up("small") {
        margin-left: 0;
        width: 100%;
      }
    }

    > .form-label:nth-of-type(2) {
      margin-left: 0;
    }
    select{
      max-width: 280px;
      border-color: #c9cac6;
      border-radius: 0;
    }
  }

  .form-checkbox,
  [data-product-attribute="set-rectangle"] .form-radio {
    border: 0;
    clip: rect(1px, 1px, 1px, 1px);
    height: 1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px;
  }
  [data-product-attribute="set-radio"]{
    
    .form-radio  + label{
      padding-left:30px;
      position: relative;
      cursor: pointer;
      &:before{
        content:' ';
        width: 20px;
        height: 20px;
        border-radius: 50%;
        border: 1px solid $color-cta3;
        position: absolute;
        left: 0;
      }
      &:after{
        content:' ';
        width: 16px;
        height: 16px;
        margin: 2px;
        border-radius: 50%;
        position: absolute;
        left: 0;
        background-color: transparent;
        padding: 2px;
      }
    }
    .form-radio:checked  + label{
      &:before{
        background-color: transparent;
      }
      &:after{
        background-color: $color-cta1;
      }

    }

    .form-radio{
      border: 0;
      clip: rect(1px, 1px, 1px, 1px);
      height: 1px;
      overflow: hidden;
      padding: 0;
      position: absolute;
      width: 1px;
      
    }
  }

  .form-option-variant {
    display: inline-block;
    font-size: 1rem;
    min-width: 2.71429rem;
    padding: 1px 0.35714rem;
    pointer-events: none;
    text-align: center;
  }

  .form-action {
    width: 100%;
    margin: auto;
    text-align: center;

    @include media-breakpoint-up("md") {
      float: none;
      padding: 0;
      width: auto;
      text-align: left;
    }

    @include media-breakpoint-up("large") {
      display: inline-block;
      margin-top: 10px;
    }

    .button {
      width: 100%;

      @include media-breakpoint-up("large") {
        margin-right: spacing("half");
        width: auto;
      }
    }
  }

  .required{
    opacity: 0;
    width: 1px;
    position: absolute;
  }
}

[data-product-attribute] .form-option {
  overflow: hidden;
  position: relative;
}

.form-option {
  display: inline-flex;
  min-height: 34px;
  min-width: 34px;
  align-items: center;
  padding-left: 4px;
  padding-right: 4px;

  border: 1px solid $color-cta3;
  // border-radius: 4px;
  color: #666;
  cursor: pointer;
  font-size: 0;
  margin-bottom: 0.5rem;
  margin-right: 0.5rem;
  padding: 1px;
  transition: all 0.15s ease;

  &.unavailable{
    background: none !important;
    // background: linear-gradient(to bottom right, #fff calc(50% - 1px), $color-cta2, #fff calc(50% + 1px) );
    color: $color-cta2;
    cursor: not-allowed !important;
    &:after,&:before{
      display: block;
      font-size: 41px;
      position: absolute;
      width: 100%;
      text-align: center;
      font-weight: 300;
      line-height: 1;
      content: ' ' !important;
      color: #dbdada !important;
      border-top: 1px solid !important;
      transform: rotate(45deg);
      opacity: 0.8;
    }
    &:after{
      transform: rotate(134deg);
    }
  }
}

.form-radio:checked + .form-option.unavailable {
  border-color: $color-cta2;
  // box-shadow: 0 0 0 1px $color-cta2;
  box-shadow: unset;
}


.form-radio:checked + .form-option {
  border-color: $primary;
  // box-shadow: 0 0 0 1px $primary;
  box-shadow: unset;
}

// Product list (with images)
// -----------------------------------------------------------------------------

.productOptions-list {
  list-style: none;
  margin: 0;
}

.productOptions-list-item {
  .form-label {
    text-align: left;
    top: -2px; // 1
  }
}

.productOptions-list-item-figure,
.productOptions-list-item-content {
  display: table-cell;
  vertical-align: middle;
}

// Share products
// -----------------------------------------------------------------------------
.shareProduct {
  text-align: center;
}

// EasyZoom settings for zoomable product image
// -----------------------------------------------------------------------------
.productView-image .easyzoom-flyout {
  overflow: hidden;
  position: absolute;
  width: 100%;
  height: 100%;
  top:0;
  left:0;
  img {
    background: #fff;
    max-width: none;
    width: auto;
  }
}

.productView-price {
  font-size: 18px;
  font-weight: 400;

}
.price.price--withoutTax {
  font-size: $h2-font-size;
  line-height: 36px;
}
.price--discounted,
.price--non-sale,
.price--rrp {
  text-decoration: line-through $color-cta1;
}
.price-section--saving {
  font-size: 16px;
}
.price-section--group {
  justify-content: center;
}
.productView-price {
  .price-section--group {
    justify-content: unset;
  }
}
//  
#form-action-addToCart {
  text-transform: uppercase;
  min-width: 320px;
  width: 100%;
  max-width: 340px;
  min-height: 60px;
  border-radius: 100px;
  background: $color-cta1;
  border-color: $color-cta1;
  display: flex;
  justify-content: center;
  align-items: center;
  @include media-breakpoint-up("md") {
    width: 350px;
  }
  i.fa-shopping-cart  {
    color: #fff;
    font-size: 22px;
    margin-right: 10px;
    
  }

}
.form-wishlist{
  position: relative;
  .btn{
    display: inline-flex;
